DROP TABLE IF EXISTS `#__PNDatabase_wishingpots`;
DROP TABLE IF EXISTS `#__PNDatabase_outfits`;

CREATE TABLE `#__PNDatabase_wishingpots` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(40) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=12 ;


CREATE TABLE `#__PNDatabase_outfits` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(40) NOT NULL,
  `pictureface` varchar(250) NOT NULL,
  `picturepose` varchar(250) NOT NULL,
  `gender` varchar(1) NOT NULL,
  `release` varchar(20) NOT NULL,
  `collstrength` tinyint(3) unsigned NOT NULL,
  `collagility` tinyint(3) unsigned NOT NULL,
  `collstamina` tinyint(3) unsigned NOT NULL,
  `initstrength` smallint(5) unsigned NOT NULL,
  `initagility` smallint(5) unsigned NOT NULL,
  `initstamina` smallint(5) unsigned NOT NULL,
  `growthstrength` double unsigned NOT NULL,
  `growthagility` double unsigned NOT NULL,
  `growthstamina` double unsigned NOT NULL,
  `bmvstrength` tinyint(3) unsigned NOT NULL,
  `bmvagility` tinyint(3) unsigned NOT NULL,
  `bmvstamina` tinyint(3) unsigned NOT NULL,
  `pot` int(11) NOT NULL,
  `published` tinyint(1) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=188 ;

